Drone.prototype.takeOff = function() {
  console.log('Taking off... prepare for pain');

  this.findCharacteristic('fa0b').write(new Buffer([0x02,0x05,0x02,0x00,0x01,0x00]), true);
  var self = this;
  setTimeout(function() {
