  }, 2000);
};

Drone.prototype.findCharacteristic = function(unique_uuid_segment) {
  var theChars = this.characteristics.filter(function(characteristic) {
    return characteristic.uuid.search(new RegExp(unique_uuid_segment)) != -1;
  });

  return theChars[0];
};

